<template lang="pug">
  .page
    .page__bd.custom-layout
      .custom-sign.custom-center
        button.custom-btnSign(v-if="!isLogin", open-type="setUserInfo", :bindgetuserinfo="getUserInfo")
          i.fa.fa-sign-in.custom-space
          |
          | 登录
        button.custom-btnSign(v-else, v-on:click="loginOut")
          i.fa.fa-sign-out.custom-space
          |
          | 退出
      .custom-info
        .custom-userIcon
          image.custom-avatar(:src="isLogin ? userInfo.avatarUrl : defaultIcon")
        .custom-bg
        .custom-userInfo
          .custom-userInfo-left.custom-center
            span.custom-text.custom-lineHeight(v-if="isLogin") {{ userInfo.nickName }}
            span.custom-text.custom-lineHeight(v-else) 请先登录
          .custom-userInfo-right
            .custom-sginIn
              i.fa.fa-calendar(:aria-hidden="true")
              span.custom-date {{ currentDate }}
              span.custom-btn(v-if="isLogin") 签到
              span.custom-btn.custom-borderWhite(v-else)
    .page__bd.custom-top
      .weui-grids
        block(v-for="item in grids", :key="index")
          navigator.weui-grid.custom-grid(url="", hover-class="weui-grid_active")
            i.weui-grid__icon.custom-gridIcon.custom-center.custom-iconSize.fa(:class="item.className", :style="item.color", :aria-hidden="true")
              .weui-grid__label.custom-top.custom-font {{ item.name }}


</template>

<script>
export default {
  data () {
    return {
      defaultIcon: 'data:image/jpeg;base64,',
      userInfo: {},
      isLogin: false,
      grids: [
        { className: 'fa-bell', name: '提醒', color: 'color:#39B444' },
        { className: 'fa-heart', name: '喜欢', color: 'color:#F02644' },
        { className: 'fa-book', name: '日记', color: 'color:#1F6CE3' },
        { className: 'fa-picture-o', name: '相册', color: 'color:#F69D23' },
        { className: 'fa-caret-square-o-right', name: '电影·电视·综艺', color: 'color:#F69D23' },
        { className: 'fa-bookmark', name: '读书', color: 'color:#39B444' },
        { className: 'fa-music', name: '音乐', color: 'color:#F36F38' },
        { className: 'fa-cog', name: '设置', color: 'color:#B8CDD4' }
      ]
    }
  },
  methods: {
    getUserInfo () {
      // 调用登录接口
      wx.login({
        success: (res) => {
          wx.getUserInfo({
            success: (res) => {
              this.setUserInfo(res)
            }
          })
        }
      })
    },
    setUserInfo (res) {
      this.isLogin = true
      this.userInfo = res.userInfo
    },
    loginOut () {
      Object.assign(this.data, this.$option.data)
    }
  },
  created: function () {
    this.getUserInfo()
  },
  computed: {
    currentDate () {
      let date = new Date()
      return date.toISOString().slice(5, 10)
    }
  }
}
</script>

